const telephone = document.querySelectorAll('.telephone');

if (telephone.length) {
  Inputmask({
    "mask": "+7 (999) 999 - 99 - 99",
    "showMaskOnHover": false,
    "onBeforeWrite": function (event, buffer, caretPos, opts) {
      return buffer;
    },
    "onKeyDown": function (event, buffer, caretPos, opts) {
    }
  }).mask(telephone);

  telephone.forEach(input => {
    function wrapPlusSeven() {
      const parent = input.closest('.form__input');
      if (parent) {
        const placeholder = parent.querySelector('.form__placeholder span');
        if (placeholder && !placeholder.closest('.phone-prefix')) {
          const span = document.createElement('span');
          span.className = 'phone-prefix';
          placeholder.parentNode.insertBefore(span, placeholder);
          span.appendChild(placeholder);
        }
      }
    }

    function checkFilled() {
      const parent = input.closest('.form__input');
      if (parent) {
        const rawValue = input.value.replace(/[^\d+]/g, '');
        if (rawValue.length > 2) {
          parent.classList.add('filled');
        } else {
          parent.classList.remove('filled');
        }
      }
    }

    function clearFilled() {
      const parent = input.closest('.form__input');
      if (parent) {
        parent.classList.remove('filled');
      }
    }

    input.addEventListener('focus', wrapPlusSeven);
    input.addEventListener('input', () => {
      wrapPlusSeven();
      checkFilled();
    });
    checkFilled();
    input.addEventListener('blur', checkFilled);
    input.clearFilled = clearFilled;
  });
}

