function closeSpollerInColumn(column) {
  const activeSpollerTitle = column.querySelector('[data-spoller]._spoller-active');
  if (activeSpollerTitle) {
    const spollersBlock = activeSpollerTitle.closest('[data-spollers]');
    const spollerSpeed = spollersBlock?.dataset.spollersSpeed ? parseInt(spollersBlock.dataset.spollersSpeed) : 500;

    activeSpollerTitle.classList.remove('_spoller-active');

    const spollerItem = activeSpollerTitle.closest('.spollers__item, .menu__item');
    if (spollerItem) {
      spollerItem.classList.remove('_spoller-active');
    }

    const contentBlock = activeSpollerTitle.nextElementSibling;
    if (contentBlock && typeof _slideUp === 'function') {
      _slideUp(contentBlock, spollerSpeed);
    } else {
      contentBlock.hidden = true;
    }
  }
}

function syncSpollerWithColumn(column) {
  const spollerTitle = column.querySelector('[data-spoller]');
  if (spollerTitle) {
    const spollersBlock = spollerTitle.closest('[data-spollers]');
    const oneSpoller = spollersBlock.hasAttribute('data-one-spoller');
    const spollerSpeed = spollersBlock?.dataset.spollersSpeed ? parseInt(spollersBlock.dataset.spollersSpeed) : 500;

    if (column.classList.contains('active') && !spollerTitle.classList.contains('_spoller-active')) {
      if (oneSpoller) {
        document.querySelectorAll('[data-spoller]._spoller-active').forEach(activeSpoller => {
          if (activeSpoller.closest('[data-spollers]') === spollersBlock) {
            activeSpoller.classList.remove('_spoller-active');
            const activeItem = activeSpoller.closest('.spollers__item, .menu__item');
            if (activeItem) activeItem.classList.remove('_spoller-active');
            _slideUp(activeSpoller.nextElementSibling, spollerSpeed);
          }
        });
      }

      spollerTitle.classList.add('_spoller-active');
      const spollerItem = spollerTitle.closest('.spollers__item, .menu__item');
      if (spollerItem) spollerItem.classList.add('_spoller-active');
      _slideDown(spollerTitle.nextElementSibling, spollerSpeed);
    }
  }
}

